mapper.insert:
INSERT 
	INTO CART(cart_num,member_id, product_num, product_count)
	VALUES(
		null
		, #{sId}
		, #{proNum}
		, #{proCount}
	)

mapper.select:
SELECT *
FROM CART
WHERE product_num = #{proNum} AND member_id = #{sId}

SELECT SUM(product_price) as allPrice, COUNT(product_num) count
FROM CART
WHERE member_id = #{sId}

