mapper.select:
SELECT r.*, p.product_name
FROM REVIEW r
JOIN PRODUCT p ON r.product_num = p.product_num
WHERE r.member_id = #{member_id}
ORDER BY r.review_date DESC
limit #{pageMaker.pageStart}, #{pageMaker.perPageNum}

mapper.update:
UPDATE RESERVATION
SET reservation_cancel = 1
WHERE reservation_num = #{reservationNum}

mapper.select:
WITH TMP AS ( SELECT * FROM POINT WHERE member_id = #{memberId}
AND point_date BETWEEN #{startDate} AND date_format(date('2023-11-06') + 1, '%Y-%m-%d') -- 테이블 칼럼명에 맞춰 바꾸기!!
ORDER BY point_date DESC LIMIT #{startRow} , #{listLimit} ) SELECT * FROM TMP
UNION ALL SELECT '' , '1' , member_point , '회원가입 축하 포인트' , member_insert_date , member_id FROM MEMBER where member_id = #{memberId}
